'use strict';

angular.module('app', ['ui.router']).config(['$stateProvider', '$urlRouterProvider', routing]);

function routing($stateProvider, $urlRouterProvider) {

    $urlRouterProvider.otherwise('/');

    $stateProvider.state('chart1', {
        url: '/',
        templateUrl: './html/chart1/chart1.html',
        controller: 'chart1Controller'
    }).state('test', {
        url: '/test',
        templateUrl: './html/chart1/chart1.html',
        controller: 'chart1Controller'
    });
}
'use strict';

angular.module('app').service('friendService', ['$q', '$http', friendService]);

function friendService($q, $http) {

    this.getFriends = function () {

        return $http.get('/api/friends').then(function (response) {
            return response.data;
        });
    };

    this.getFriendById = function (id) {

        return $http.get('/api/friends/' + id).then(function (response) {

            console.log(response);
            return response.data;
        }, function (error) {

            console.log(error);
            return "sorry, there is nobody by that id";
        });
    };

    this.postNewFriend = function (friend) {

        return $http.post('/api/friends', friend).then(function (response) {

            console.log(response);
            return "friend added!";
        }, function (error) {
            console.log(error);
            return error;
        });
    };
}
'use strict';

